package com.example.hr;

import static org.junit.jupiter.api.Assertions.assertEquals;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.CsvSource;

import com.example.hr.config.ModelMapperConfig;
import com.example.hr.domain.Department;
import com.example.hr.domain.Employee;
import com.example.hr.domain.FiatCurrency;
import com.example.hr.domain.JobStyle;
import com.example.hr.dto.HireEmployeeRequest;

public class ModelMapperConverterTests {

	@ParameterizedTest
	@CsvSource({
		"35373330300,Jack,Bauer,TR440006278659712762534346,100000,EUR,IT,1965,FULL_TIME,"
	})
	void hireEmployeeRequest2EmployeeConverterTest(String csvIdentity,String csvFirstName,String csvLastName,
			String csvIban,double csvSalary,String csvCurrency,String csvDepartment,
			int csvBirthYear,String csvJobStyle,String csvPhoto) {
		// CUT: ModelMapper -> Converter
		// 1. Test Fixture/Setup
		var config = new ModelMapperConfig();
		var mapper = config.mapper();
		var request = new HireEmployeeRequest();
		request.setIdentity(csvIdentity);
		request.setFirstName(csvFirstName);
		request.setLastName(csvLastName);
		request.setIban(csvIban);
		request.setSalary(csvSalary);
		request.setCurrency(FiatCurrency.valueOf(csvCurrency));
		request.setDepartment(Department.valueOf(csvDepartment));
		request.setBirthYear(csvBirthYear);
		request.setJobStyle(JobStyle.valueOf(csvJobStyle));
		request.setPhoto(csvPhoto);
		// 2. Call Exercise Method
		var convertedEmployee = mapper.map(request, Employee.class);
		// 3. Verification
		assertEquals(csvIdentity, convertedEmployee.getTcKimlikNo().getValue());
		assertEquals(csvFirstName, convertedEmployee.getFullName().getFirstName());
		assertEquals(csvLastName, convertedEmployee.getFullName().getLastName());
		assertEquals(csvIban, convertedEmployee.getIban().getValue());
		// 4. Tear-down
	}
}
